    function getUrl(url, params){
        if(params){
            var paramarray = [];
            for(var k in params){
                paramarray.push(k + "=" + encodeURIComponent(params[k]));
            }
            return url + "?" + paramarray.join("&");
        }else{
            return url;
        }
    }

    function persistGetPageJSON(url, callback){
        var rtn = {};
        function repeatGetPage(url, callback){
            var reqobj = GetPage(url, function(response){
                try{
                    var responseJSON = JSON.parse(response);
                }catch(e){ // if server having problems and not returning json
                    var timeoutID = setTimeout(function(){
                        repeatGetPage(url, callback);
                    }, 5000);
                    rtn.abort = function(){
                        clearTimeout(timeoutID);
                    };
                }
                callback(responseJSON);
            });
            rtn.abort = reqobj.abort.bind(reqobj);
        }
        repeatGetPage(url, callback);
        return rtn;
    }

    function GetPage(url, callback) {
        if(debug) console.log(url);
        return abortableXHR({
            method: "GET",
            url: url,
            synchronous: false,
            timeout: 0,
            onload: function (resp) {
                callback(resp.responseText);
            },
            ontimeout: function () {
                callback(null);
            }
        });
    }

    function abortableXHR(details){
        var aborted = false;
        var abortableOnload = (resp) => {
            if(!aborted && details.onload){
                details.onload(resp);
            }
        };
        var abortableOntimeout = (resp) => {
            if(!aborted && details.ontimeout){
                details.ontimeout(resp);
            }
        };
        GMX.xmlHttpRequest({
            method: details.method,
            url: details.url,
            synchronous: details.synchronous,
            timeout: details.timeout,
            onload: abortableOnload,
            ontimeout: abortableOntimeout
        });
        return {abort: () => { aborted = true; }};
    }

